package stringdemo2;

public class StringBufferDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		StringBuffer str = new StringBuffer("Hello");
		
		System.out.println(str);
		
		str.append(" Testing");
		
		System.out.println(str);
		
		str.reverse();
		
		System.out.println(str);
		
		
		
		

	}

}
